import React from 'react';
import "./App.css";

export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      input: "",
      history: []
    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.clearList = this.clearList.bind(this);
  }

  handleChange(event) {
    this.setState({
      input: event.target.value
    })
  }

  handleSubmit(event) {
    event.preventDefault();
    if (this.state.input !== "") {
      const history = [...this.state.history];
      history.push({
        input: this.state.input
      })

      this.setState({
        input: "",
        history: history
      })
      localStorage.setItem("history", JSON.stringify(history));
    } else {
      alert("Please enter a task!");
    }
  }

  componentDidMount() {
    const savedHistory = JSON.parse(localStorage.getItem("history"));
    if (savedHistory) {
      this.setState({
        history: savedHistory
      })
    }
  }

  clearList() {
    localStorage.clear()
    this.setState({
      history: []
    })
  }

  delete(index) {
    const history = [...this.state.history];
    history.splice(index, 1);
    this.setState({
      history: history
    })
    localStorage.setItem("history", JSON.stringify(history));
  }

  render() {
    return (
      <div>

        <div className="jumbotron jumbotron-fluid p-8 mb-5 bg-info text-white">
          <div className="container">
            <h1 className="display-4">To Do List</h1>
            <p className="lead">mpla mpla mpla</p>
            <form onSubmit={this.handleSubmit}>
              <div className="row">
                <div className="input-group mb-3">
                  <input className="form-control" placeholder="Enter new task" type="text" value={this.state.input} onChange={this.handleChange} />
                  <div className="input-group-append">
                    <button className="btn btn-secondary btn-lg btn" type="submit">Add</button>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </div>

        {this.state.history.map((item, index) => {
          return (
            <div key={index}>
              <div className="container">
                <div className="row">

                  <div className="col">
                    <input type="checkbox" />
                  </div>

                  <div className="col-6">
                    <ul>
                      <li key={index}>
                        <span>{item.input}</span>
                      </li>
                    </ul>
                  </div>

                  <div className="col">
                    <button onClick={this.delete.bind(this, index)}>Delete</button>
                  </div>

                </div>
              </div>
            </div>
          )
        })}

        <div className="row">
          {this.state.history.length > 0 ? (
            <div>
              <button className="btn btn-secondary" onClick={this.clearList}>Clear List</button>
            </div>
          ): null}
        </div>

      </div>
    )
  }
}
