import React from 'react';

export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      input: "",
      history: []
    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.clearList = this.clearList.bind(this);
  }

  handleChange(event) {
    this.setState({
      input: event.target.value
    })
  }

  handleSubmit(event) {
    event.preventDefault();
    if (this.state.input !== "") {
      const history = [...this.state.history];
      history.push({
        input: this.state.input
      })

      this.setState({
        input: "",
        history: history
      })
      localStorage.setItem("history", JSON.stringify(history));
    } else {
      alert("Please enter a task!");
    }
  }

  componentDidMount() {
    const savedHistory = JSON.parse(localStorage.getItem("history"));
    if (savedHistory) {
      this.setState({
        history: savedHistory
      })
    }
  }

  clearList() {
    localStorage.clear()
    this.setState({
      history: []
    })
  }

  delete(index) {
    const history = [...this.state.history];
    history.splice(index, 1);
    this.setState({
      history: history
    })
    localStorage.setItem("history", JSON.stringify(history));
  }

  render() {
    return (
      <div>

        <div className="jumbotron jumbotron-fluid p-8 mb-5 bg-info text-white">
          <div className="container">
            <div className="row">
              <div className="col-12 col-md-4">
                <h1 className="display-4">To Do List</h1>
                <p className="lead">mpla mpla mpla</p>
                <form onSubmit={this.handleSubmit}>
                    <input placeholder="Enter new task" type="text" value={this.state.input} onChange={this.handleChange} />
                    <button type="submit">Add</button>
                </form>
              </div>
            </div>
          </div>
        </div>

          <div className="row justify-content-center">
            <div className="col-12 col-md-4">

            </div>
          </div>

          <div className="row">
            {this.state.history.length > 0 ? (
              <div>
                <p>To Do List:</p>
                <button onClick={this.clearList}>Clear List</button>
              </div>
            ): null}
          </div>

          {this.state.history.map((item, index) => {
            return (
              <div key={index}>
                <ul>
                  <input type="checkbox"/>
                  <li key={index}>
                    <span>{item.input}</span>
                    <button onClick={this.delete.bind(this, index)}>Delete</button>
                  </li>
                </ul>
              </div>
            )
          })}
      </div>
    )
  }
}
